mapper.select:
   select * from cst_customer 

       cust_name like CONCAT('%',#{name},'%') and cust_region = #{region}


        cust_name like CONCAT('%',#{name},'%')


        cust_region = #{region}

    and   1=1
LIMIT #{current},#{pageSize}

select count(1) from cst_customer


    cust_name like CONCAT('%',#{name},'%') and cust_region = #{region}


     cust_name like CONCAT('%',#{name},'%')


     cust_region = #{region}

and   1=1

mapper.update:
update cst_customer set cust_name = #{cust_name}, cust_region = #{cust_region}, cust_manager_id = #{cust_manager_id}, cust_manager_name = #{cust_manager_name}, cust_level = #{cust_level}, cust_level_label =
'小客户' '中客户' '大客户' '合作伙伴' '战略合作伙伴'
, cust_satisfy = #{cust_satisfy}, cust_credit = #{cust_credit}, cust_addr = #{cust_addr}, cust_zip = #{cust_zip}, cust_tel = #{cust_tel}, cust_fax = #{cust_fax}, cust_website = #{cust_website}, cust_licence_no = #{cust_licence_no}, cust_chieftain = #{cust_chieftain}, cust_bankroll = #{cust_bankroll}, cust_turnover = #{cust_turnover}, cust_bank = #{cust_bank}, cust_bank_account = #{cust_bank_account}, cust_local_tax_no = #{cust_local_tax_no}, cust_national_tax_no = #{cust_national_tax_no} where cust_no = #{cust_no};
